DROP PROCEDURE IF EXISTS `TestProcedure`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `TestProcedure`(
	IN _username VARCHAR (20),
	IN _fullname VARCHAR (100),
	IN _gender VARCHAR(20),
	IN _orderby VARCHAR(10),
	IN _limit INT,
	IN _offset INT,
	OUT total INT
)
BEGIN
	SELECT SQL_CALC_FOUND_ROWS
		id,
		username,
		fullname,
		gender,
		birthday,
		level
	FROM
		users
	WHERE
		deleted IS NULL
		AND username LIKE CONCAT('%', _username , '%')
		AND fullname LIKE CONCAT('%', _fullname , '%')
		AND gender IN(_gender)
	ORDER BY _orderby
	LIMIT _limit OFFSET _offset ;
	SELECT
		FOUND_ROWS() INTO total;
END
;;
DELIMITER ;

